<script setup>
  import { useDate } from 'vuetify'
  import { ref } from 'vue'

  const date = ref(new Date('2018-03-02'))
  const adapter = useDate()

  function allowedDates (val) {
    return parseInt(adapter.toISO(val).split('-')[2], 10) % 2 === 0
  }
</script>

