                { id: 402, title: 'Brandon' },
                { id: 403, title: 'Sean' },
              ],
            },
          ],
        },
      ],
      open: [1, 2],
      search: null,
      caseSensitive: false,
    }),

    methods: {
      filter (value, search, item) {
        return this.caseSensitive ? value.indexOf(search) > -1 : value.toLowerCase().indexOf(search.toLowerCase()) > -1
      },
    },
  }
</script>

